ounded Total"}, {"fieldtype": "Section Break", "label": "Supplier Address"}, {"fieldtype": "Column Break"}, {"fieldname": "address_display", "print_hide": 0, "label": "Address"}, {"fieldtype": "Column Break"}, {"fieldname": "contact_display", "print_hide": 0, "label": "Contact"}, {"fieldname": "contact_mobile", "print_hide": 0, "label": "Mobile No"}, {"fieldtype": "Section Break", "label": "Company Billing Address"}, {"fieldtype": "Column Break"}, {"fieldname": "billing_address", "print_hide": 0, "label": "Billing Address"}, {"fieldtype": "Column Break"}, {"fieldname": "billing_address_display", "print_hide": 0, "label": "Billing Address"}, {"fieldname": "terms", "print_hide": 0, "label": "Terms and Conditions"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<table class="table table-bordered">
