css:
.print-format{
    font-size:11px;
    font-family: 'D-DIN';
    margin-left:5px;
    margin-right:5px;
    margin-top:2px;
}

#rcorners1 {
  border-radius: 25px;
  background: lightgray;
  width: 98%;
  padding: 8px;
  height: 100%;
}

#rcorners-poly {
  border-radius: 25px;
  background: lightgray;
  width: 98%;
  padding: 8px;
  height: 100%;
}
.wrap {
    white-space: pre-wrap; /* css-3 */
        white-space: -moz-pre-wrap; /* Mozilla, since 1999 */
        white-space: -pre-wrap; /* Opera 4-6 */
        white-space: -o-pre-wrap; /* Opera 7 */
        word-wrap: break-word; /* Internet Explorer 5.5+ */
}

.row {
    display: table;
}

.row [class*="col-"] {
    float: none;
    display: table-cell;
    vertical-align: top;
}

.table-border>thead>tr>th, .table-border>tbody>tr>th, .table-border>tfoot>tr>th, .table-border>thead>tr>td, .table-border>tbody>tr>td, .table-border>tfoot>tr>td {
padding-top: 1px !important;
padding-bottom: 1px !important;
padding-right: 1px !important;
padding-left: 8px !important;
border:0.5px solid #E95328;
}

.table-border1>thead>tr>th, .table-border1>tbody>tr>th, .table-border1>tfoot>tr>th, .table-border1>thead>tr>td, .table-border1>tbody>tr>td, .table-border1>tfoot>tr>td {
border-collapse: collapse;
border:1px solid #732E85;
position: static;
margin-left: auto;
margin-right: auto;
padding-top: 1px !important;
padding-bottom: 1px !important;
padding-right: 1px !important;
padding-left: 8px !important;
}

.container1 { 
  position: right;
  top: 8px;
  padding-right: 1px;
  font-size: 18px;
  width:210px;
}
.container2 { 
  position: right;
  top: 8px;
  padding-right: 8px;
  font-size: 18px;
  width:210px;
}
.table-condensed.th{
    font-weight: bold;
}
.table-condensed>thead>tr>th, .table-condensed>tbody>tr>th, .table-condensed>tfoot>tr>th, .table-condensed>thead>tr>td, .table-condensed>tbody>tr>td, .table-condensed>tfoot>tr>td {
padding-top: 2px !important;
padding-bottom: 2px !important;
padding-right: 5px !important;
padding-left: 5px !important;


}
.table-condensed tr:nth-child(even) {
    background-color: #DFEDDB;}
.table-condensed tr:nth-child(odd) {
    background-color:#FF7F50;

}
#bottom-left{
     vertical-align:bottom;
      margin-left: auto;
      margin-right: auto;
}
