css:
.divider:before,
.divider:after {
    content: "";
    flex: 1;
    border-bottom: 2px solid red;
    margin: auto;
}
.red-box {
  background-color: #e20026;
  width: 60px;
  padding : 5px;
  color: white;
}
#myFooter {
    background-color: #e20026 !important;
    padding: 10px;

}
.line {
  position: relative;
  padding-left:25rem;
}
.line:after {
  content: "";
  border-bottom: 2.5px solid #e20026;
  position: absolute;
  color:#e20026;
  top: 50%;
  left: 0;
  right: 0;
  width: 100%;
  z-index: -1;
}
.line span {
  padding: 0 8px;
  background-color: white;
  color:#e20026;
  position: relative;
}
.footer {
   position: sticky;
   left: 0;
   bottom: 10px;
   width: 100%;
   background-color: red;
   color: white;
   text-align: center;
}
h1{

margin: 100px auto;
text-align: right;
font-size: 40px;
max-width: 600px;
position:relative;
padding-left:5rem;
}
h2:before{
content: "";
display:block;
width: 404px;
height:2px;
background: red;
position: absolute;
left: 0;
top: 16%;
z-index: -2;
}
p{
    /*font-size:8px;*/
    content:justify;
}
h2:after{
content: "";
display:block;
width: 63px;
height:2px;
background: red;
position: absolute;
right: 0;
top: 16%;
z-index: -2;
}
