.print-format {
	font-size:10px;
	font-family: 'Soleto';
    margin-top: 5px;
    margin-left: 0mm;
    margin-right: 0mm;

}

.table-spec>thead>tr>th, .table-spec>tbody>tr>th, .table-spec>tfoot>tr>th, .table-spec>thead>tr>td, .table-spec>tbody>tr>td, .table-spec>tfoot>tr>td {
padding-top: 1px !important;
padding-bottom: 1px !important;
border:0.5px solid #4d004d;

}

.table-spec tr:first-child {
   border-left: 0px;
}
.table-spec td:first-child {
   border-left: 0px;
}

.table-spec td:last-child {
  border-right: 0px;
}
.table-spec tr:last-child {
  border-bottom: 0.5px solid #4d004d;
}

.table-striped > tr:nth-child(2n+1) > td, .table-striped > tr:nth-child(2n+1) > th {
   background-color: #e6e6e6;
}

.table-condensed>thead>tr>th, .table-condensed>tbody>tr>th, .table-condensed>tfoot>tr>th, .table-condensed>thead>tr>td, .table-condensed>tbody>tr>td, .table-condensed>tfoot>tr>td {
padding: 1px !important;
padding-left: 5px;
border-left: 0px;
border-right: 0px;
}
.table-condensed {
    border-left: 0px;
    border-right: 0px;
    border-collapse: collapse;
    border-style: hidden;
    border-bottom: 0.5px solid #4d004d;
}

.table-border>thead>tr>th, .table-border>tbody>tr>th, .table-border>tfoot>tr>th, .table-border>thead>tr>td, .table-border>tbody>tr>td, .table-border>tfoot>tr>td {
padding-left: 5px;
padding-top: 1px !important;
padding-bottom: 1px !important;
border:0.5px solid #4d004d;

}

.table-border tr:first-child {
   border-left: 0px;
}
.table-border td:first-child {
   border-left: 0px;
}

.table-border td:last-child {
  border-right: 0px;
}
.table-border tr:last-child {
  border-bottom: 0.5px solid #4d004d;
}


.column {
  float: left;
  width: 50%;
  padding: 5px;
}
.row::before {
    padding-top:0px;
}
/* Clear floats after image containers */
.row::after {
  content: "";
  clear: both;
  display: table;
}
