css:
.divider:before,
.divider:after {
    content: "";
    flex: 1;
    border-bottom: 2px solid red;
    margin: auto;
}
.red-box {
  background-color: #e20026;
  width: 200px;
  padding :2px;
  color: white;
}
.line {
  position: relative;
  padding-left:24rem;
}
.line:after {
  content: "";
  border-bottom: 2.5px solid #e20026;
  position: absolute;
  color:#e20026;
  top: 50%;
  left: 0;
  right: 0;
  width: 100%;
  z-index: -1;
}
.line span {
  padding: 0 8px;
  background-color: white;
  color:#e20026;
  position: relative;
}
.header {
  width:100%;
  height:100px;
  background: #26b;
  color: white;
  position:top;
  top:0;
  left:0;
  transition: height 500ms, background 500ms;
}
.header.tiny {
  height:10px;
  background:white;
}


h1{

margin: 100px auto;
text-align: right;
font-size: 40px;
max-width: 600px;
position:relative;
padding-left:5rem;
}
h2:before{
content: "";
display:block;
width: 404px;
height:2px;
background: red;
position: absolute;
left: 0;
top: 15.5%;
z-index: -2;
}
p{
    font-size:8px;
    content:justify;
}
#foo{
    padding-top:200px;
}
h2:after{
content: "";
display:block;
width: 100px;
height:2px;
background: red;
position: absolute;
right: 0;
top: 15.5%;
z-index: -2;
}
