css:
.divider:before,
.divider:after {
    content: "";
    flex: 1;
    border-bottom: 2px solid red;
    margin: auto;
}
.red-box {
  background-color: #e20026;
  width: 253px;
  padding : 5px;
  color: white;
}


.line {
  position: relative;
  padding-left:29rem;
}
.line:after {
  content: "";
  border-bottom: 2.5px solid #e20026;
  position: absolute;
  color:#e20026;
  top: 90%;
  left: 0;
  right: 0;
  width: 100%;
  z-index: -1;
}
.line span {
  padding: 0 8px;
  background-color: white;
  color:#e20026;
  position: relative;
}
