.line {
  position: relative;
  padding-left:33rem;
}
.line:after {
  content: "";
  border-bottom: 2.5px solid #e20026;
  position: absolute;
  color:#e20026;
  top: 50%;
  left: 0;
  right: 0;
  width: 100%;
  z-index: -1;
}
.line span {
  padding: 0 8px;
  background-color: white;
  color:#e20026;
  position: relative;
}


